import { useEffect, useRef, useState } from 'react';
import './App.css';


function App() {

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const enable = username && password;

  const ref = useRef()

  useEffect(() => {
    let images = ref.current.querySelectorAll("img"),
      total = images.length,
      current = 0

    const imageslider = () => {
      if (current > 1) {
        images[current - 1].classList.add("opacity-0");
      } else {
        images[total - 1].classList.add("opacity-0");
      }
      images[current].classList.remove("opacity-0");
      if (current === total - 1) {
        current = 0
      } else {
        current += 1
      }
    }
    imageslider()

    let interval = setInterval(imageslider, 3000)
    return () => {
      clearInterval(interval)
    };



  }, [ref])

  return (
    <>
      <div className='w-full h-full flex gap-x-8 items-center justify-center'>
        <div className='w-[380px] h-[581px] relative bg-logo-pattern bg-[length:468.32px_634.15px] bg-[-46px_0px]'>
          <div className='absolute top-[27px] right-[18px] w-[250px] h-[538px]' ref={ref}>
            <img className='w-full h-full absolute top-0 left-0 opacity-0 transition-opacity duration-1000 ease-linear' src="https://www.instagram.com/static/images/homepage/screenshots/screenshot1-2x.png/cfd999368de3.png" alt='' />
            <img className='w-full h-full absolute top-0 left-0 opacity-0 transition-opacity duration-1000 ease-linear' src="https://www.instagram.com/static/images/homepage/screenshots/screenshot2-2x.png/80b8aebdea57.png" alt='' />
            <img className='w-full h-full absolute top-0 left-0 opacity-0 transition-opacity duration-1000 ease-linear' src="https://www.instagram.com/static/images/homepage/screenshots/screenshot3-2x.png/fe2540684ab2.png" alt='' />
            <img className='w-full h-full absolute top-0 left-0 opacity-0 transition-opacity duration-1000 ease-linear' src="https://www.instagram.com/static/images/homepage/screenshots/screenshot4-2x.png/8e9224a71939.png" alt='' />
          </div>
        </div>


        <div className='w-[350px] bg-white border p-[40px] pt-8 pb-2'>
          <a href='#' className='flex justify-center'>
            <div className='h-[51px] w-[175px] bg-[0px_0px]  bg-[length:176px_181px] bg-no-repeat	inline-block bg-logo-instagram' />
          </a>
          <form className='mt-5 !gap-y-1.5'>
            <label htmlFor='info' className='relative'>
              <input type="text" required={true} onChange={(e) => setUsername(e.target.value)} className="bg-zinc-50 border w-full h-[38px] outline-none text-xs mt-1 focus:border-gray-300 peer rounded" style={{ padding: "9px 0 7px 8px" }} id='info' />
              <small className='absolute top-0 left-2 text-xs text-[#8e8e8e]  duration-200 peer-focus:text-[10px]   peer-valid:-translate-y-[10px]'>Phone number, username or email</small>
            </label>
            <label htmlFor='floating_password' className='relative'>
              <input type="password" required={true} onChange={(e) => setPassword(e.target.value)} className="bg-zinc-50 border w-full h-[38px] outline-none text-xs mt-1 focus:border-gray-300 peer rounded" style={{ padding: "9px 0 7px 8px" }} id='floating_password' />
              <small className='absolute top-0 left-2 text-xs text-[#8e8e8e]  duration-200 peer-focus:text-[10px]   peer-valid:-translate-y-[10px]'>Password</small>
            </label>
            <button type='submit' disabled={!enable} className='h-[30px] bg-brand text-white text-sm w-full rounded mt-2 font-medium disabled:opacity-50'>Log In</button>
            <div className='flex items-center mt-3'>
              <div className='h-px bg-gray-300 flex-1' />
              <span className='px-[18px] font-semibold text-gray-500 text-sm'>OR</span>
              <div className='h-px bg-gray-300 flex-1' />
            </div>
          </form>
        </div>
      </div>
    </>
  );
}

export default App;
