import React, { useState } from 'react';
import { Icon } from '../../icon';
import SearchMenuOpen from './SearchMenuOpen';

const Search = () => {

    const [open, setOpen] = useState(false);
    return (
        <>
            <button className="px-3 py-3  rounded-3xl duration-150 ease-linear flex items-center text-link gap-x-4 font-normal group hover:bg-linkHover"
                onClick={() => setOpen(!open)}
            >
                <div className='group-hover:scale-[1.1] duration-150'>
                    <Icon name="search" />
                </div>
                <span>Ara</span>
            </button>
            {open && <SearchMenuOpen />}
        </>
    );
};

export default Search;
