import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import More from '../Components/Menu/More.js';
import Menu from './Sidebar/Menu';

const Sidebar = () => {
    const [open, setOpen] = useState(false);

    return (
        <aside className={`w-[244px] h-full px-3 pt-2 pb-5 border-r border-[#dbdbdb] duration-150 ease-linear flex flex-col ${open ? "w-[73px]" : "dura"}`}>
            <Link to="/" className='h-[92px] w-full flex items-center px-3'>
                <img src='/img/logo.svg' className='w-[103px] h-[29px]' alt='' />
            </Link>
            <Menu open={open} setOpen={setOpen} />
            <More />
        </aside>
    );
};

export default Sidebar;
