import React, { useEffect, useState } from 'react';
import HomesIcerik from '../Components/HomesIcerik';
import Slider from "react-slick";
import Hikaye from '../data/hikaye.json';
import { AiOutlineArrowLeft, AiOutlineArrowRight } from "react-icons/ai";

function useWindowSize() {
    const [size, setSize] = useState([window.innerHeight, window.innerWidth]);
    useEffect(() => {
        const handleResize = () => setSize(window.innerHeight, window.innerWidth);
        window.addEventListener("resize", handleResize);
    }, [])
    return size;
}

const array = Hikaye.map((x) => {
    return x.image
})
console.log(array);


const HomeInstagram = () => {
    const NextArrow = ({ onClick }) => {
        return (
            <div className="arrow next" onClick={onClick}>
                <AiOutlineArrowRight />
            </div>
        );
    };

    const PrevArrow = ({ onClick }) => {
        return (
            <div className="arrow prev" onClick={onClick}>
                <AiOutlineArrowLeft />
            </div>
        );
    };

    const [imageIndex, setImageIndex] = useState(0);
    const [height, width] = useWindowSize();

    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        nextArrow: <NextArrow />,
        prevArrow: <PrevArrow />,
        beforeChange: (current, next) => {
            console.log(current);
            setImageIndex(next);
        }
    };

    return (
        <>
            <div className='w-[630px] mr-[64px] App'>
                <div className='inline-flex items-center gap-x-3 mb-6'>
                    <Slider {...settings}>
                        <div>
                            {Hikaye.map((story) => (
                                <div key={story.id} className={story === imageIndex ? "slide activeSlide" : "slide"}>
                                    <div className='w-[66px] h-[66px] border-2 border-[#ff0c5e] rounded-full flex items-center justify-center'>
                                        <img src={story.image} alt='' className='w-14 h-14 object-contain rounded-full' />
                                    </div>
                                    <span className='text-xs leading-4 font-normal'>{story.title}</span>
                                </div>
                            ))}
                        </div>
                    </Slider>
                </div>
                <HomesIcerik />
            </div>
        </>
    );
};

export default HomeInstagram;
