import React, { useState } from 'react';
import SearchItemUser from './SearchItemUser';

const SearchMenuOpen = ({ open }) => {
    const [search, setSearch] = useState([]);


    return (
        <div className={`w-[397px]  h-screen pt-2  rounded-r-2xl  duration-300 ease-linear  fixed top-0 bg-white shadow-3xl ${open ? "left-[73px] opacity-100 z-20 " : "-left-[73px] opacity-0 z-[-1]"}`}>
            <div className='pt-3 pb-9 pr-[14px] pl-6'>
                <h2 className='text-2xl font-semibold'>Ara</h2>
            </div>
            <div className='flex items-center mb-6 pb-6 relative px-4 border-b'>
                <input type="text" onChange={e => setSearch(e.target.value)} placeholder='Ara' className='py-1 px-4 h-10 w-full rounded-lg bg-[#efefef] placeholder:text-[#8e8e8e] outline-none ' />
                {/* <button type='buton' className='absolute right-[10px] top-1/2 -translate-x-2/4 -translate-y-2/4 bg-[#c7c7c7] rounded-full  leading-none text-sm w-4 h-4 m-0 p-0 text-white flex items-center justify-center'>×</button> */}
            </div>
            <div className='flex justify-between items-center mx-6 mb-2'>
                <span className='font-semibold'>Yakındakiler</span>
                <span className='text-sm font-semibold text-[#0095f6] cursor-pointer'>Tümünü Temizle</span>
            </div>
            <SearchItemUser search={search} setSearch={setSearch} />
        </div>
    );
};

export default SearchMenuOpen;
