import React from 'react';

const Input = ({ label, ...props }) => {
    return (
        <label className='block relative'>
            <input required={true} className="bg-zinc-50 border w-full h-[38px] outline-none text-xs mt-1 focus:border-gray-300 peer rounded" style={{ padding: "9px 0 7px 8px" }} id='info' {...props} />
            <small className='absolute top-0 left-2 text-xs text-[#8e8e8e]  duration-200 peer-focus:text-[10px]   peer-valid:-translate-y-[10px]'>{label}</small>
        </label>
    );
};

export default Input;
