import React from 'react';
import Create from '../Menu/Create.js';
import Discover from '../Menu/Discover.js';
import HomeBar from '../Menu/HomeBar.js';
import Message from '../Menu/Message.js';
import Notifications from '../Menu/Notifications.js';
import Profile from '../Menu/Profile.js';
import Reels from '../Menu/Reels.js';
import Search from '../Menu/Search';

const Menu = ({ open }) => {
    const IsActive = ({ isActive }) => { return "px-3 py-3  rounded-3xl duration-150 ease-linear flex items-center text-link gap-x-4 font-normal group hover:bg-linkHover " + (isActive ? '!font-bold' : "text-link") }
    return (
        <>
            <div className='grow gap-y-2 flex flex-col'>
                <HomeBar menuActive={IsActive} />
                <Search open={open} />
                <Discover menuActive={IsActive} />
                <Reels menuActive={IsActive} />
                <Message menuActive={IsActive} />
                <Notifications />
                <Create />
                <Profile menuActive={IsActive} />
            </div>
        </>
    );
};

export default Menu;
