import React from 'react';
import { Link } from 'react-router-dom';
import More from '../Components/Menu/More.js';
import Menu from './Sidebar/Menu';

const Sidebar = () => {



    openNav = () => {
        this.setState({
            navwidth: '250px'
        });
    }

    closeNav = () => {
        this.setState({
            navwidth: '0px'
        });
    }


    return (
        <>
            <button onClick={this.openNav}>Menu</button>
            <aside className="w-[244px] h-full px-3 pt-2 pb-5 border-r border-[#dbdbdb] flex flex-col">
                <Link to="/" className='h-[92px] w-full flex items-center px-3'>
                    <img src='/img/logo.svg' className='w-[103px] h-[29px]' alt='' />
                </Link>
                <Menu />
                <More />
            </aside>
        </>
    );
};

export default Sidebar;
