import React, { useEffect, useRef, useState } from 'react';
import HomesIcerik from '../Components/HomesIcerik';
import Hikaye from '../data/hikaye.json';
import ScrollContainer from 'react-indiana-drag-scroll';


const HomeInstagram = () => {
    const storyRef = useRef();
    const [prev, setPrev] = useState(false);
    const [next, setNext] = useState(false);

    useEffect(() => {

        if (storyRef.current) {
            const scrollHandle = () => {
                const isEnd = storyRef.current.scrollLeft + storyRef.current.offsetWidth === storyRef.current.scrolWidth
                const isBegin = storyRef.current.scrollLeft === 0
                setPrev(!isBegin)
                setNext(!isEnd)
            }
            scrollHandle()
            storyRef.current.addEventListener('scroll', scrollHandle)

            return () => {
                storyRef.current.removeEventListener('scroll', scrollHandle)
            }

        }
    }, [storyRef])

    const nextStory = () => {
        storyRef.current.scrollLeft += storyRef.current.offsetWidth - 200
    }
    const prevStory = () => {
        storyRef.current.scrollLeft -= storyRef.current.offsetWidth - 200
    }

    return (
        <>
            <div className='w-[630px] mr-[64px] relative'>
                <ScrollContainer
                    className="scroll-container relative flex scroll-smooth overflow-x-auto flex-nowrap gap-x-3 mb-6 items-center"
                    innerRef={storyRef}
                >
                    {prev && <button onClick={prevStory}><img src='/img/prev.svg' alt='' className='w-7 h-7 absolute left-0 top-1/2 bg-black' /></button>}
                    {next && <button onClick={nextStory}><img src='/img/next.svg' alt='' className='w-7 h-7 absolute right-0 top-1/2 bg-black' /></button>}
                    {Hikaye.map((story) => (
                        <div key={story.id}>
                            <div className='w-[66px] h-[66px] border-2 border-[#ff0c5e] rounded-full flex items-center justify-center'>
                                <img src={story.image} alt='' className='w-14 h-14 object-contain rounded-full' />
                            </div>
                            <span className='text-xs leading-4 font-normal'>{story.title}</span>
                        </div>
                    ))}
                </ScrollContainer>
                <HomesIcerik />
            </div>
        </>
    );
};

export default HomeInstagram;
