import React from 'react';
import { Link } from 'react-router-dom';
import expo from '../data/explores.json';
import { Icon } from '../icon';
import Masonry, { ResponsiveMasonry } from "react-responsive-masonry";


const Explores = () => {
    return (

        <ResponsiveMasonry
            columnsCountBreakPoints={{ 315: 1, 750: 2, 900: 3 }}
        >
            <Masonry>
                {expo.map((kesfet) => (
                    <div key={kesfet.id} className='shrink-0  relative group'>
                        <img src={kesfet.image} alt='' className='w-full h-[315px]  object-cover ' />
                        <button type='button' className='absolute top-2 right-2'>
                            <Icon name="video" />
                        </button>
                        <div className='absolute w-full bottom-0 h-0 z-10 bg-black bg-opacity-50 duration-200  group-hover:h-full'></div>
                    </div>
                ))}
            </Masonry>
        </ResponsiveMasonry>
    );
};

export default Explores;
