import React from 'react';
import { Link } from 'react-router-dom';
import Create from '../views/Create.js';
import Discover from '../views/Discover.js';
import HomeBar from '../views/HomeBar.js';
import Message from '../views/Message.js';
import More from '../views/More.js';
import Notifications from '../views/Notifications.js';
import Profile from '../views/Profile.js';
import Reels from '../views/Reels.js';
import Search from '../views/Search.js';

const Sidebar = () => {

    const isActive = ({ isActive }) => { return "px-3 py-3  rounded-3xl duration-150 ease-linear flex items-center text-link gap-x-4 font-normal group hover:bg-linkHover " + (isActive ? 'fw-bold' : "") }

    return (
        <aside className='w-[244px] h-full px-3 pt-2 pb-5 border-r border-[#dbdbdb] flex flex-col'>
            <Link to="/" className='h-[92px] w-full flex items-center px-3'>
                <img src='/img/logo.svg' className='w-[103px] h-[29px]' alt='' />
            </Link>
            <div className='grow gap-y-2 flex flex-col'>
                <HomeBar />
                <Search isActive={isActive} />
                <Discover />
                <Reels />
                <Message />
                <Notifications />
                <Create />
                <Profile />
            </div>
            <More />
        </aside>
    );
};

export default Sidebar;
