import React from 'react';
import { NavLink } from 'react-router-dom';
import { Icon } from '../../icon';

const HomeBar = ({ menuActive, open, active, handleClick }) => {
    return (
        <NavLink to={"/"} onClick={() => handleClick(1)} className={active === 1 ? "font-bold" : "px-3 py-3  rounded-3xl duration-150 ease-linear flex items-center text-link gap-x-4 font-normal group hover:bg-linkHover"}>
            <div className='group-hover:scale-[1.1] duration-150 '>
                <Icon name="home" />
            </div>
            {!open &&
                <span>Ana Sayfa</span>
            }
        </NavLink>
    );
};

export default HomeBar;
